import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Eye, 
  Download, 
  Heart, 
  Share2, 
  X, 
  ChevronLeft, 
  ChevronRight,
  ZoomIn,
  ZoomOut,
  RotateCw,
  Info,
  Camera,
  Calendar,
  MapPin,
  Lock,
  Unlock,
  Mail
} from 'lucide-react';
import { toast } from 'react-hot-toast';
import axios from 'axios';
import Masonry from 'react-masonry-css';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import 'react-lazy-load-image-component/src/effects/blur.css';
import './GalleryView.css';

const GalleryView = ({ galleryId: propGalleryId, isSharedView = false }) => {
  const { galleryId: paramGalleryId } = useParams();
  const [searchParams] = useSearchParams();
  const token = searchParams.get('token');
  const password = searchParams.get('password');

  // Use prop galleryId if provided, otherwise use URL param
  const galleryId = propGalleryId || paramGalleryId;
  // For shared view, treat galleryId as token
  const actualToken = isSharedView ? galleryId : token;

  const [gallery, setGallery] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [showLightbox, setShowLightbox] = useState(false);
  const [showInfo, setShowInfo] = useState(false);
  const [zoom, setZoom] = useState(1);
  const [rotation, setRotation] = useState(0);
  const [favorites, setFavorites] = useState(new Set());
  const [shareLink, setShareLink] = useState('');
  const [passwordRequired, setPasswordRequired] = useState(false);
  const [enteredPassword, setEnteredPassword] = useState('');
  const [showPasswordModal, setShowPasswordModal] = useState(false);

  const lightboxRef = useRef(null);
  const imageRef = useRef(null);

  // Masonry breakpoints
  const breakpointColumnsObj = {
    default: 4,
    1100: 3,
    700: 2,
    500: 1
  };

  // Fetch gallery data
  const fetchGallery = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const url = actualToken
        ? `/api/share/${actualToken}`
        : `/api/galleries/${galleryId}/public`;

      const response = await axios.get(url, {
        params: actualToken ? { ...(password ? { password } : {}) } : undefined,
      });
      const data = response.data;

      setGallery(data.gallery);
      setPhotos(data.photos || []);
      setShareLink(data.shareLink || '');
    } catch (err) {
      if (err.response?.status === 401 && err.response?.data?.requiresPassword) {
        setPasswordRequired(true);
        setShowPasswordModal(true);
        return;
      }
      console.error('Error fetching gallery:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load gallery');
    } finally {
      setLoading(false);
    }
  }, [galleryId, actualToken, password]);

  useEffect(() => {
    fetchGallery();
  }, [fetchGallery]);

  // Handle password submission
  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    const newUrl = new URL(window.location);
    newUrl.searchParams.set('password', enteredPassword);
    window.location.href = newUrl.toString();
  };

  // Open lightbox
  const openLightbox = (photo, index) => {
    setSelectedPhoto(photo);
    setLightboxIndex(index);
    setShowLightbox(true);
    setZoom(1);
    setRotation(0);
    document.body.style.overflow = 'hidden';
  };

  // Close lightbox
  const closeLightbox = () => {
    setShowLightbox(false);
    setSelectedPhoto(null);
    setShowInfo(false);
    setZoom(1);
    setRotation(0);
    document.body.style.overflow = 'auto';
  };

  // Navigate lightbox
  const navigateLightbox = (direction) => {
    const newIndex = direction === 'next' 
      ? (lightboxIndex + 1) % photos.length
      : (lightboxIndex - 1 + photos.length) % photos.length;

    setLightboxIndex(newIndex);
    setSelectedPhoto(photos[newIndex]);
    setZoom(1);
    setRotation(0);
  };

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (!showLightbox) return;

      switch (e.key) {
        case 'Escape':
          closeLightbox();
          break;
        case 'ArrowLeft':
          navigateLightbox('prev');
          break;
        case 'ArrowRight':
          navigateLightbox('next');
          break;
        case 'i':
        case 'I':
          setShowInfo(!showInfo);
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [showLightbox, showInfo, lightboxIndex]);

  // Toggle favorite
  const toggleFavorite = (photoId) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(photoId)) {
      newFavorites.delete(photoId);
    } else {
      newFavorites.add(photoId);
    }
    setFavorites(newFavorites);
  };

  // Share gallery
  const shareGallery = async () => {
    try {
      if (navigator.share) {
        await navigator.share({
          title: gallery.title,
          text: gallery.description,
          url: window.location.href
        });
      } else {
        await navigator.clipboard.writeText(window.location.href);
        toast.success('Gallery link copied to clipboard!');
      }
    } catch (err) {
      console.error('Error sharing:', err);
      toast.error('Failed to share gallery');
    }
  };

  // Download photo
  const downloadPhoto = async (photo) => {
    try {
      const response = await axios.get(`/photos/${photo._id}/download`, {
        params: token ? { token } : undefined,
        responseType: 'blob',
      });
      const blob = response.data;
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = photo.filename || `photo-${photo._id}.jpg`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast.success('Photo downloaded successfully!');
    } catch (err) {
      console.error('Error downloading photo:', err);
      toast.error('Failed to download photo');
    }
  };

  // Handle inquiry about a photo (duplicate function to be removed)
  const handleInquiryDuplicate = (photo) => {
    const subject = `Inquiry about "${photo.title || 'Artwork'}"`;
    const body = `Hi,\n\nI'm interested in learning more about the artwork "${photo.title || 'this piece'}"${photo.metadata?.artworkInfo ? ` (${photo.metadata.artworkInfo.medium}${photo.metadata.artworkInfo.year ? `, ${photo.metadata.artworkInfo.year}` : ''})` : ''}.\n\n${photo.metadata?.purchaseInfo?.price ? `Listed price: $${photo.metadata.purchaseInfo.price.toLocaleString()}\n\n` : ''}Please provide more information about:\n- Availability\n- Pricing details\n- Shipping information\n- Any additional details\n\nThank you!`;

    const mailtoLink = `mailto:?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    window.open(mailtoLink, '_blank');
  };

  // Format date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // Format camera settings
  const formatCameraSettings = (exif) => {
    if (!exif) return null;

    const settings = [];
    if (exif.fNumber) settings.push(`f/${exif.fNumber}`);
    if (exif.exposureTime) settings.push(`${exif.exposureTime}s`);
    if (exif.iso) settings.push(`ISO ${exif.iso}`);
    if (exif.focalLength) settings.push(`${exif.focalLength}mm`);

    return settings.length > 0 ? settings.join(' â€¢ ') : null;
  };

  // Handle inquiry about a photo
  const handleInquiry = (photo) => {
    const photographer = gallery?.photographer;
    const photographerName = photographer?.name || 'the photographer';
    const photographerEmail = photographer?.email || '';

    const subject = `Inquiry about "${photo.title || 'Untitled Photo'}"`;
    const body = `Hi ${photographerName},\n\n` +
      `I'm interested in your photo "${photo.title || 'Untitled Photo'}" from your gallery "${gallery?.title || 'your gallery'}".\n\n` +
      `Could you please provide more information about:\n` +
      `- Pricing and available sizes/prints\n` +
      `- Licensing options\n` +
      `- Delivery timeline\n` +
      `- Any other details about this piece\n\n` +
      `Thank you for your time.\n\n` +
      `Best regards,\n` +
      `[Your Name]`;

    const mailtoLink = `mailto:${photographerEmail}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    window.open(mailtoLink, '_blank');
  };

  if (loading) {
    return (
      <div className="gallery-loading">
        <div className="loading-spinner"></div>
        <p>Loading gallery...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="gallery-error">
        <h2>Error Loading Gallery</h2>
        <p>{error}</p>
        <button onClick={fetchGallery} className="retry-button">
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="gallery-view">
      {/* Password Modal */}
      <AnimatePresence>
        {showPasswordModal && (
          <motion.div 
            className="password-modal-overlay"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div 
              className="password-modal"
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
            >
              <div className="password-modal-header">
                <Lock className="lock-icon" />
                <h2>Password Required</h2>
                <p>This gallery is password protected</p>
              </div>

              <form onSubmit={handlePasswordSubmit} className="password-form">
                <input
                  type="password"
                  value={enteredPassword}
                  onChange={(e) => setEnteredPassword(e.target.value)}
                  placeholder="Enter password"
                  className="password-input"
                  autoFocus
                  required
                />
                <button type="submit" className="password-submit">
                  <Unlock className="unlock-icon" />
                  Access Gallery
                </button>
              </form>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Gallery Header */}
      {gallery && (
        <div className="gallery-header">
          <div className="gallery-info">
            <h1 className="gallery-title">{gallery.title}</h1>
            {gallery.description && (
              <p className="gallery-description">{gallery.description}</p>
            )}

            <div className="gallery-meta">
              {gallery.photographer && (
                <div className="photographer-info">
                  <Camera className="meta-icon" />
                  <span>by {gallery.photographer.name}</span>
                </div>
              )}

              <div className="photo-count">
                <Eye className="meta-icon" />
                <span>{photos.length} photos</span>
              </div>

              {gallery.createdAt && (
                <div className="gallery-date">
                  <Calendar className="meta-icon" />
                  <span>{formatDate(gallery.createdAt)}</span>
                </div>
              )}
            </div>
          </div>

          <div className="gallery-actions">
            <button onClick={shareGallery} className="action-button">
              <Share2 className="action-icon" />
              Share
            </button>
          </div>
        </div>
      )}

      {/* Photo Grid */}
      {photos.length > 0 ? (
        <Masonry
          breakpointCols={breakpointColumnsObj}
          className="photo-masonry"
          columnClassName="photo-masonry-column"
        >
          {photos.map((photo, index) => (
            <motion.div
              key={photo._id}
              className="photo-item"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              onClick={() => openLightbox(photo, index)}
            >
              <div className="photo-container">
                <LazyLoadImage
                  src={photo.previewUrl || photo.thumbnailUrl || photo.url}
                  alt={photo.title || 'Gallery photo'}
                  effect="blur"
                  className="photo-image"
                  placeholderSrc={photo.thumbnailUrl}
                />

                <div className="photo-overlay">
                  <div className="photo-actions">
                    <button 
                      className={`favorite-button ${favorites.has(photo._id) ? 'active' : ''}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleFavorite(photo._id);
                      }}
                    >
                      <Heart className="action-icon" />
                    </button>

                    <button 
                      className="download-button"
                      onClick={(e) => {
                        e.stopPropagation();
                        downloadPhoto(photo);
                      }}
                    >
                      <Download className="action-icon" />
                    </button>
                  </div>

                  <div className="photo-info">
                    {photo.title && (
                      <div className="photo-title">{photo.title}</div>
                    )}

                    {photo.metadata && (
                      <div className="photo-metadata">
                        {photo.metadata.description && (
                          <p className="photo-description">{photo.metadata.description}</p>
                        )}

                        {photo.metadata.artworkInfo && (
                          <div className="artwork-info">
                            {photo.metadata.artworkInfo.medium && (
                              <span className="artwork-medium">{photo.metadata.artworkInfo.medium}</span>
                            )}
                            {photo.metadata.artworkInfo.dimensions && (
                              <span className="artwork-dimensions">{photo.metadata.artworkInfo.dimensions}</span>
                            )}
                            {photo.metadata.artworkInfo.year && (
                              <span className="artwork-year">{photo.metadata.artworkInfo.year}</span>
                            )}
                          </div>
                        )}

                        {photo.metadata.purchaseInfo && photo.metadata.purchaseInfo.price && (
                          <div className="photo-price">
                            ${photo.metadata.purchaseInfo.price.toLocaleString()}
                          </div>
                        )}

                        {photo.metadata.purchaseInfo && photo.metadata.purchaseInfo.available && (
                          <button 
                            className="inquiry-button"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleInquiry(photo);
                            }}
                          >
                            Inquire
                          </button>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </Masonry>
      ) : (
        <div className="empty-gallery">
          <Camera className="empty-icon" />
          <h3>No photos in this gallery</h3>
          <p>This gallery doesn't contain any photos yet.</p>
        </div>
      )}

      {/* Lightbox */}
      <AnimatePresence>
        {showLightbox && selectedPhoto && (
          <motion.div 
            className="lightbox-overlay"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeLightbox}
            ref={lightboxRef}
          >
            <div className="lightbox-container" onClick={(e) => e.stopPropagation()}>
              {/* Lightbox Header */}
              <div className="lightbox-header">
                <div className="lightbox-title">
                  {selectedPhoto.title || `Photo ${lightboxIndex + 1} of ${photos.length}`}
                </div>

                <div className="lightbox-controls">
                  <button 
                    className="lightbox-button"
                    onClick={() => setShowInfo(!showInfo)}
                    title="Toggle Info"
                  >
                    <Info className="control-icon" />
                  </button>

                  <button 
                    className="lightbox-button"
                    onClick={() => downloadPhoto(selectedPhoto)}
                    title="Download"
                  >
                    <Download className="control-icon" />
                  </button>

                  <button 
                    className="lightbox-button"
                    onClick={closeLightbox}
                    title="Close"
                  >
                    <X className="control-icon" />
                  </button>
                </div>
              </div>

              {/* Lightbox Content */}
              <div className="lightbox-content">
                {/* Navigation */}
                <button 
                  className="lightbox-nav lightbox-nav-prev"
                  onClick={() => navigateLightbox('prev')}
                  disabled={photos.length <= 1}
                >
                  <ChevronLeft className="nav-icon" />
                </button>

                <button 
                  className="lightbox-nav lightbox-nav-next"
                  onClick={() => navigateLightbox('next')}
                  disabled={photos.length <= 1}
                >
                  <ChevronRight className="nav-icon" />
                </button>

                {/* Image */}
                <div className="lightbox-image-container">
                  <img
                    ref={imageRef}
                    src={selectedPhoto.url}
                    alt={selectedPhoto.title || 'Gallery photo'}
                    className="lightbox-image"
                    style={{
                      transform: `scale(${zoom}) rotate(${rotation}deg)`
                    }}
                  />
                </div>

                {/* Zoom Controls */}
                <div className="lightbox-zoom-controls">
                  <button 
                    className="zoom-button"
                    onClick={() => setZoom(Math.max(0.5, zoom - 0.25))}
                    disabled={zoom <= 0.5}
                  >
                    <ZoomOut className="zoom-icon" />
                  </button>

                  <span className="zoom-level">{Math.round(zoom * 100)}%</span>

                  <button 
                    className="zoom-button"
                    onClick={() => setZoom(Math.min(3, zoom + 0.25))}
                    disabled={zoom >= 3}
                  >
                    <ZoomIn className="zoom-icon" />
                  </button>

                  <button 
                    className="rotate-button"
                    onClick={() => setRotation((rotation + 90) % 360)}
                  >
                    <RotateCw className="rotate-icon" />
                  </button>
                </div>
              </div>

              {/* Photo Info Panel */}
              <AnimatePresence>
                {showInfo && (
                  <motion.div 
                    className="photo-info-panel"
                    initial={{ x: '100%' }}
                    animate={{ x: 0 }}
                    exit={{ x: '100%' }}
                    transition={{ type: 'spring', damping: 25, stiffness: 200 }}
                  >
                    <div className="info-header">
                      <h3>Photo Information</h3>
                      <button 
                        className="info-close"
                        onClick={() => setShowInfo(false)}
                      >
                        <X className="close-icon" />
                      </button>
                    </div>

                    <div className="info-content">
                      {selectedPhoto.title && (
                        <div className="info-item">
                          <label>Title</label>
                          <span>{selectedPhoto.title}</span>
                        </div>
                      )}

                      {selectedPhoto.description && (
                        <div className="info-item">
                          <label>Description</label>
                          <span>{selectedPhoto.description}</span>
                        </div>
                      )}

                      {selectedPhoto.dateTaken && (
                        <div className="info-item">
                          <label>Date Taken</label>
                          <span>{formatDate(selectedPhoto.dateTaken)}</span>
                        </div>
                      )}

                      {selectedPhoto.location && (
                        <div className="info-item">
                          <label>Location</label>
                          <span>{selectedPhoto.location}</span>
                        </div>
                      )}

                      {selectedPhoto.exifData && (
                        <>
                          {selectedPhoto.exifData.camera && (
                            <div className="info-item">
                              <label>Camera</label>
                              <span>{selectedPhoto.exifData.camera}</span>
                            </div>
                          )}

                          {selectedPhoto.exifData.lens && (
                            <div className="info-item">
                              <label>Lens</label>
                              <span>{selectedPhoto.exifData.lens}</span>
                            </div>
                          )}

                          {formatCameraSettings(selectedPhoto.exifData) && (
                            <div className="info-item">
                              <label>Settings</label>
                              <span>{formatCameraSettings(selectedPhoto.exifData)}</span>
                            </div>
                          )}
                        </>
                      )}

                      <div className="info-item">
                        <label>Dimensions</label>
                        <span>{selectedPhoto.width} Ã— {selectedPhoto.height}</span>
                      </div>

                      {selectedPhoto.fileSize && (
                        <div className="info-item">
                          <label>File Size</label>
                          <span>{(selectedPhoto.fileSize / 1024 / 1024).toFixed(2)} MB</span>
                        </div>
                      )}

                      {/* Add inquiry button */}
                      <div className="info-item inquiry-section">
                        <button 
                          className="inquiry-button"
                          onClick={() => handleInquiry(selectedPhoto)}
                        >
                          <Mail className="inquiry-icon" />
                          Inquire About This Photo
                        </button>
                      </div>
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
             </div>
           </motion.div>
         )}
       </AnimatePresence>
     </div>
   );
 };

 export default GalleryView;
