import React from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';

const ShareGalleryPage = () => {
  const { token } = useParams();

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center h-16">
            <h1 className="text-2xl font-serif font-bold gradient-text">
              Gallery Pavilion
            </h1>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <div className="text-center py-20">
            <h1 className="text-3xl font-serif font-bold text-gray-900 mb-4">
              Private Gallery
            </h1>
            <p className="text-gray-600 mb-8">
              Share Token: {token}
            </p>
            <p className="text-gray-500">
              This page will display the shared gallery with masonry grid,
              lightbox functionality, and photo metadata panels.
            </p>
          </div>
        </motion.div>
      </main>
    </div>
  );
};

export default ShareGalleryPage;
