import React, { useEffect, useMemo, useState } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import { ArrowLeft, Camera, Calendar, MapPin, Info, Mail, ShoppingCart, Sun, Moon } from 'lucide-react';

const prefersDark = () => window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;

const PhotoDetailPage = () => {
  const { token, photoId } = useParams();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const [photo, setPhoto] = useState(null);
  const [gallery, setGallery] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Theme: default to system
  const [theme, setTheme] = useState(prefersDark() ? 'dark' : 'light');
  useEffect(() => {
    const mq = window.matchMedia('(prefers-color-scheme: dark)');
    const handler = (e) => setTheme(e.matches ? 'dark' : 'light');
    mq.addEventListener('change', handler);
    return () => mq.removeEventListener('change', handler);
  }, []);

  const toggleTheme = () => setTheme((t) => (t === 'dark' ? 'light' : 'dark'));

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);
        // We can fetch photo by id; the backend allows optional auth and public access if permitted
        const params = {};
        // If there was a password or invite on gallery, propagate via query if present
        const password = searchParams.get('password');
        const invite = searchParams.get('invite');
        if (password) params.password = password;
        if (invite) params.invite = invite;

        // Fetch the shared gallery to get photographer contact and ensure visibility
        const galleryResp = await axios.get(`/share/${token}`, { params });
        const g = galleryResp.data.gallery || galleryResp.data;
        const respPhotographer = galleryResp.data.photographer || g?.photographer;
        const normalizedPhotographer = respPhotographer && typeof respPhotographer === 'object'
          ? {
              ...respPhotographer,
              name: respPhotographer.name || `${respPhotographer.firstName || ''} ${respPhotographer.lastName || ''}`.trim(),
              email: respPhotographer.email || respPhotographer.clientEmail || undefined,
            }
          : respPhotographer || undefined;
        const normalizedGallery = {
          ...g,
          photographer: normalizedPhotographer || (typeof g?.photographer === 'object'
            ? {
                ...g.photographer,
                name: g.photographer.name || `${g.photographer.firstName || ''} ${g.photographer.lastName || ''}`.trim(),
              }
            : g?.photographer),
        };
        setGallery(normalizedGallery);

        // Fetch the specific photo
        const photoResp = await axios.get(`/photos/${photoId}`, { params });
        setPhoto(photoResp.data);
      } catch (err) {
        console.error('Failed to load photo detail', err);
        setError(err.response?.data?.message || 'Failed to load photo details');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [token, photoId, searchParams]);

  const handleInquiry = () => {
    if (!photo) return;
    const recipient = gallery?.photographer?.email || '';
    const subject = `Inquiry about \"${photo.title || 'Artwork'}\"`;
    const body = `Hi,\n\nI'm interested in \"${photo.title || 'this piece'}\".\n\n${photo.metadata?.purchaseInfo?.price ? `Listed price: $${photo.metadata.purchaseInfo.price.toLocaleString()}\n\n` : ''}Please share details about availability, pricing, and shipping.\n\nPhoto ID: ${photo._id}\nGallery: ${gallery?.title || ''}\nShare Link: ${window.location.origin}/gallery/${token}/photo/${photo._id}\n\nThank you!`;
    const mailtoLink = `mailto:${encodeURIComponent(recipient)}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    window.open(mailtoLink, '_blank');
  };

  const handlePurchase = () => {
    if (!photo) return;
    const recipient = gallery?.photographer?.email || '';
    const subject = `Purchase request: \"${photo.title || 'Artwork'}\"`;
    const body = `Hello,\n\nI would like to purchase \"${photo.title || 'this piece'}\".${photo.metadata?.purchaseInfo?.price ? `\n\nPrice seen: $${photo.metadata.purchaseInfo.price.toLocaleString()}` : ''}\n\nPlease advise next steps for payment and delivery.\n\nPhoto ID: ${photo._id}\nGallery: ${gallery?.title || ''}\nShare Link: ${window.location.origin}/gallery/${token}/photo/${photo._id}\n\nRegards,`;
    const mailtoLink = `mailto:${encodeURIComponent(recipient)}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
    window.open(mailtoLink, '_blank');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-neutral-100 dark:bg-black text-neutral-800 dark:text-neutral-100">
        <div className="animate-spin h-10 w-10 border-4 border-neutral-300 border-t-transparent rounded-full" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center gap-4 bg-neutral-100 dark:bg-black text-neutral-800 dark:text-neutral-100">
        <p className="text-lg">{error}</p>
        <button onClick={() => navigate(-1)} className="px-4 py-2 rounded-md bg-neutral-900 text-white dark:bg-white dark:text-black">Go Back</button>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'dark' : ''} bg-gradient-to-b from-white to-neutral-100 dark:from-black dark:to-neutral-900 text-neutral-900 dark:text-neutral-50`}>      
      <div className="max-w-6xl mx-auto px-4 py-6">
        <div className="flex items-center justify-between mb-6">
          <button onClick={() => navigate(`/gallery/${token}`)} className="inline-flex items-center gap-2 text-sm px-3 py-2 rounded-full bg-neutral-200 hover:bg-neutral-300 dark:bg-neutral-800 dark:hover:bg-neutral-700">
            <ArrowLeft size={18} />
            Back to Gallery
          </button>
          <button onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')} className="inline-flex items-center gap-2 text-sm px-3 py-2 rounded-full bg-neutral-200 hover:bg-neutral-300 dark:bg-neutral-800 dark:hover:bg-neutral-700">
            {theme === 'dark' ? <Sun size={16} /> : <Moon size={16} />}
            {theme === 'dark' ? 'Light' : 'Dark'}
          </button>
        </div>

        <div className="grid lg:grid-cols-2 gap-8 items-start">
          {/* Image */}
          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4 }} className="rounded-2xl overflow-hidden shadow-2xl ring-1 ring-black/5 dark:ring-white/10 bg-white dark:bg-neutral-950">
            <img src={photo.url || photo.previewUrl || photo.thumbnailUrl} alt={photo.title || 'Photo'} className="w-full h-auto object-contain" />
          </motion.div>

          {/* Details */}
          <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4, delay: 0.05 }} className="space-y-6">
            <div>
              <h1 className="text-3xl font-semibold tracking-tight">{photo.title || 'Untitled'}</h1>
              {photo.metadata?.description && (
                <p className="mt-2 text-neutral-600 dark:text-neutral-300 leading-relaxed">{photo.metadata.description}</p>
              )}
            </div>

            {/* Artwork Info */}
            {(photo.metadata?.artworkInfo || photo.metadata?.exif || photo.metadata?.location) && (
              <div className="rounded-xl bg-white/70 dark:bg-neutral-900/60 ring-1 ring-black/5 dark:ring-white/10 p-5 grid sm:grid-cols-2 gap-4">
                {photo.metadata?.artworkInfo && (
                  <div className="space-y-2">
                    <div className="font-medium flex items-center gap-2"><Info size={16} /> Artwork Info</div>
                    <ul className="text-sm text-neutral-600 dark:text-neutral-300 space-y-1">
                      {photo.metadata.artworkInfo.medium && <li>Medium: {photo.metadata.artworkInfo.medium}</li>}
                      {photo.metadata.artworkInfo.dimensions && <li>Dimensions: {photo.metadata.artworkInfo.dimensions}</li>}
                      {photo.metadata.artworkInfo.year && <li>Year: {photo.metadata.artworkInfo.year}</li>}
                    </ul>
                  </div>
                )}
                {photo.metadata?.exif && (
                  <div className="space-y-2">
                    <div className="font-medium flex items-center gap-2"><Camera size={16} /> Camera</div>
                    <ul className="text-sm text-neutral-600 dark:text-neutral-300 space-y-1">
                      {photo.metadata.exif.cameraModel && <li>Model: {photo.metadata.exif.cameraModel}</li>}
                      {photo.metadata.exif.lensModel && <li>Lens: {photo.metadata.exif.lensModel}</li>}
                      {photo.metadata.exif.fNumber && <li>Aperture: f/{photo.metadata.exif.fNumber}</li>}
                      {photo.metadata.exif.exposureTime && <li>Shutter: {photo.metadata.exif.exposureTime}s</li>}
                      {photo.metadata.exif.iso && <li>ISO: {photo.metadata.exif.iso}</li>}
                      {photo.metadata.exif.focalLength && <li>Focal: {photo.metadata.exif.focalLength}mm</li>}
                    </ul>
                  </div>
                )}
                {photo.metadata?.location && (
                  <div className="space-y-2">
                    <div className="font-medium flex items-center gap-2"><MapPin size={16} /> Location</div>
                    <p className="text-sm text-neutral-600 dark:text-neutral-300">{photo.metadata.location}</p>
                  </div>
                )}
              </div>
            )}

            {/* Price and actions */}
            <div className="flex flex-wrap items-center gap-4">
              {photo.metadata?.purchaseInfo?.price && (
                <div className="text-2xl font-semibold tracking-tight">${photo.metadata.purchaseInfo.price.toLocaleString()}</div>
              )}
              <div className="flex items-center gap-3">
                <button onClick={handleInquiry} className="px-5 py-2 rounded-full bg-neutral-900 text-white hover:bg-black dark:bg-white dark:text-black dark:hover:bg-neutral-200">Inquiry</button>
                <button onClick={handlePurchase} className="px-5 py-2 rounded-full bg-emerald-600 text-white hover:bg-emerald-700 inline-flex items-center gap-2"><ShoppingCart size={16} /> Purchase</button>
              </div>
            </div>

            {/* Photographer */}
            {gallery?.photographer?.name && (
              <div className="text-sm text-neutral-500 dark:text-neutral-400">Photographer: <span className="font-medium text-neutral-700 dark:text-neutral-200">{gallery.photographer.name}</span>{gallery.photographer.email ? ` • ${gallery.photographer.email}` : ''}</div>
            )}
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default PhotoDetailPage;
